#include <bits/stdc++.h>using namespace std;     int main(){    int t;    cin >>t;    while(t--)    {         int n;        cin >>n;        vector <long long int> a(n);        for(int i = 0; i< n;i++)        {            cin >>a[i];        }        long long int no_op = a[0];        long long int mini = a[0];        for(int i = 1; i < n;i++)        {            if(mini > a[i])            {                mini = a[i];            }            no_op +=mini;        }        long long int op_12 = a[0]+a[1];        if(n <= 2)        {            cout << (long long )(min(no_op, op_12))<<endl;            continue;        }        long long int op_23 = a[0] + min(a[0],a[1]+a[2]);        cout << (long long)(min(no_op,min(op_12,op_23)))<<endl;                             }    return 0;}