#include <bits/stdc++.h>using namespace std;     int main(){    int t;    cin >>t;    while(t--)    {        int n, m;    long long h;     cin >> n >> m >> h;      vector<long long> orig_a(n);    vector<long long> cur_a(n);    for (int i = 0; i < n; ++i) {        cin >> orig_a[i];        cur_a[i] = orig_a[i];    }    vector<int> history;    vector<bool> in_history(n, false);     for (int i = 0; i < m; ++i) {        int b;        long long c;        cin >> b >> c;        b--;          long long new_val = cur_a[b] + c;         if (new_val > h) {                        for (int idx : history) {                cur_a[idx] = orig_a[idx];                in_history[idx] = false;            }            history.clear();         } else {            cur_a[b] = new_val;                        if (!in_history[b]) {                in_history[b] = true;                history.push_back(b);            }        }    }     for (int i = 0; i < n; ++i) {        cout << cur_a[i] <<" ";    }    cout << "\n";                    }    return 0;}