#include <bits/stdc++.h>using namespace std;   int main(){    int t;    cin >>t;    while(t--)    {          int n;          cin >>n;          string s;          cin >>s;          int cnt = 0;          vector <int>ones;          for(int i =0;i<n;i++)          {               if(s[i] == '1')               {                    ones.push_back(i);                    cnt++;               }           }           if(cnt == 0)          {               cout << (n + 2) / 3 << endl;           }          else          {                 int m = ones.size();               if(ones[0]>=1)m += (ones[0] + 1)/3;                              for(int i = 0;i < cnt-1;i++)               {                    int gap = ones[i+1] - ones[i] - 1;                    if (gap >= 3) {                         m += (gap) / 3;                                             }               }                int last_gap = n-1 - ones.back();               m += (last_gap + 1) / 3;                cout << m <<endl;           }    }    return 0;}