#include <iostream>#include <vector>#include <algorithm> using namespace std; int main() {    // Fast I/O    ios_base::sync_with_stdio(false);    cin.tie(NULL);     int t;    if (!(cin >> t)) return 0;    while (t--) {        int n;        cin >> n;        vector<int> a(n);        for (int i = 0; i < n; i++) {            cin >> a[i];        }         // Store original size because we might modify 'target' logic        int size = n;        int i = 0;        int target = n; // The value we WANT to find at the current position         // 1. Skip the prefix that is already correct (e.g., 5 4 ...)        while (i < size && a[i] == target) {            target--;            i++;        }         // CRITICAL FIX: If i == size, the array is already 5 4 3 2 1.        // We don't need to do anything else.        if (i == size) {            for (int x : a) cout << x << " ";            cout << "\n";            continue;        }         // 2. Find where the 'target' value is hiding        int r = -1;        for (int j = i; j < size; j++) {            if (a[j] == target) {                r = j;                break;            }        }         // 3. Reverse the segment to bring 'target' to position 'i'        // We reverse from current position 'i' up to where we found the target 'r'        if (r != -1) {            reverse(a.begin() + i, a.begin() + r + 1);        }         // 4. Print the entire result        for (int j = 0; j < size; j++) {            cout << a[j] << " ";        }        cout << "\n";    }    return 0;}