#include <bits/stdc++.h>using namespace std;   int main(){    int t;    cin >>t;    while(t--)    {        int n;        cin >>n;        vector<int> a(n);        for(int i = 0; i< n;i++)        {            cin >>a[i];        }         int i = 0;        while(n == a[i])        {            cout << n<<" ";            n--;            i++;        }         int cnt = 0;        int r = 0;         for(int j = i; j < n+i;j++)        {            cnt++;            if(n == a[j])            {                r = j;                break;            }        }                  reverse(a.begin()+i, a.begin()+r+1);         for(int j = i;j < n+i;j++) cout << a[j]<<" ";        cout <<endl;                   }    return 0;}