#include <bits/stdc++.h>using namespace std;   int main(){    int t;    cin >>t;    while(t--)    {        int n,q;        cin >>n>>q;        vector<int> a(n),b(n);        for(int i = 0; i< n;i++)        {            cin >>a[i];        }        for(int i = 0; i< n;i++)        {            cin >>b[i];        }         vector<pair<int, int>> queries(q);        for(int i = 0; i< q;i++)        {            cin >> queries[i].first >> queries[i].second;        }        a[n-1] = max(a[n-1],b[n-1]);        for(int i = n-2;i >= 0;i--)        {            a[i]= max({a[i],b[i], a[i+1]});        }                 long long pre[n+1] = {0};        for(int i = 1; i<= n;i++)        {            pre[i] = pre[i-1]+ a[i-1];        }        for(int i = 0; i<q;i++)        {            int l = queries[i].first;            int r = queries[i].second;            cout << pre[r] - pre[l - 1] << (i == q - 1 ? "" : " ");         }        cout <<endl;        }    return 0;}